// This script generates an HTML report using the 'multiple-cucumber-html-reporter' module

// Import the 'multiple-cucumber-html-reporter' module
const report = require("multiple-cucumber-html-reporter");

// Generate the HTML report with specified configuration
report.generate({
  // Directory containing the JSON files generated by Cypress
  jsonDir: "reports/cypress/json/",
  
  // Path to store the generated HTML report
  reportPath: "reports/cypress/html/",
  
  // Metadata information to be included in the HTML report
  metadata: {
    browser: {
      name: "Chrome",
      version: "14",
    },
    device: "Local test machine",
    platform: {
      name: "Windows",
      version: "10",
    },
    customData: {
      title: 'Run info',
      data: [
        { label: 'Project', value: 'Binance Test API' },
        { label: 'Release', value: '1.0.0' },
        { label: 'Cycle', value: 'N/A' }
        //{ label: 'Execution Start Time', value: 'Nov 19th 2017, 02:31 PM EST' },
        //{ label: 'Execution End Time', value: 'Nov 19th 2017, 02:56 PM EST' }
      ]
    }
  },
});
